# plugins/discourse-ai-extension/plugin.rb
register_asset "javascripts/discourse/initializers/add_gpt4o_model.js"

# assets/javascripts/discourse/initializers/add_gpt4o_model.js
import { withPluginApi } from "discourse/lib/plugin-api";

export default {
  name: "add-gpt4o-model",
  initialize() {
    withPluginApi("0.8", (api) => {
      const siteSettings = api.container.lookup("site-settings:main");
      const modelSetting = siteSettings.ai_openai_completions_model;
      if (modelSetting && !modelSetting.choices.includes("gpt-4o")) {
        modelSetting.choices.push("gpt-4o");
      }
    });
  },
};

require "net/http"
require "json"

module DiscourseMultiAi
  class GeminiProvider < BaseProvider
    def self.perform_completion(prompt:, model:, api_key:, temperature: 0.7)
      uri = URI("https://generativelanguage.googleapis.com/v1beta/models/#{model}:generateContent?key=#{api_key}")

      body = {
        contents: [{ parts: [{ text: prompt }] }],
        generationConfig: { temperature: temperature }
      }

      headers = { "Content-Type" => "application/json" }

      response = Net::HTTP.post(uri, body.to_json, headers)
      JSON.parse(response.body).dig("candidates", 0, "content", "parts", 0, "text")
    end
  end
end

